import React, { useState } from 'react'; // Get the state/values

import './ExpensesFilter.css';

const ExpensesFilter = () => {
    const year = new Date()
    const [preState, curState] = useState(year)

    const filter = (preState) => {
        const currentState = curState
        console.log('Worked');
        console.log(currentState);
    }


    return (
        <div className='expenses-filter'>
            <div className='expenses-filter__control'>
                <label>Filter by year</label>
                <select>
                    {/* <option value='2022'>2022</option>
                    <option value='2021'>2021</option>
                    <option value='2020'>2020</option>
                    <option value='2019'>2019</option> */}
                    <option value='2022' onChange={filter}>{preState}</option>
                    <option value='2021' onChange={filter}>{preState}</option>
                    <option value='2020' onChange={filter}>{preState}</option>
                    <option value='2019' onChange={filter}>{preState}</option>
                </select>
            </div>
        </div>
    );
};

export default ExpensesFilter;
