import React, { useState } from "react";
import './UserForm.css';

const UserForm = () => {
    const [enteredName, setEnteredName] = useState('')
    const [enteredAge, setEnteredAge] = useState('')

    const inputUserNameHandler = (event) => {
        setEnteredName(event.target.value)
    }

    const inputUserAgeHandler = (event) => {
        setEnteredAge(event.target.value)
        console.log(event.target.value);
    }



    return (
        <div className="UsersForm__">
            <div className="UserForm__input">
                <h4>Username:</h4>
                <input type="text" onChange={inputUserNameHandler}></input>
                <h4>Age (Years):</h4>
                <input type="number" onChange={inputUserAgeHandler}></input>
                <button>Add User</button>
            </div>
        </div>
    )

}

export default UserForm
